import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "./AuthContext";

const SERVER_URL = "http://localhost:8081";

const FileInfo = () => {
  const { token, setToken } = useAuth();
  const { fileId } = location.state || {}; // Извлекаем fileId из состояния
  const [error, setError] = useState("");

  const fetchProfile = async () => {
    if (!token) {
      setError("You are not authenticated or token expired!");
      setProfile(null);
      return;
    }

    try {
      const res = await axios.post(SERVER_URL + "/api/v1/profile/info",
        {
          fileId: id
        },
        {
          headers: {
            Authorization: `Bearer ${token}`
          },
          withCredentials: true
        });
      setProfile(res.data);
    } catch (error) {
      setError(`Error fetching profile: ${error.response?.data?.message || error.message}`);
      handleLogout()
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  return (
    <div>
      <h1>Info</h1>

    </div>
  );
};

export default FileInfo;
