import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "./AuthContext";

const SERVER_URL = "http://localhost:8081";

const Profile = () => {
  const { token, setToken } = useAuth();

  const [profile, setProfile] = useState(null);
  const [error, setError] = useState("");
  const [file, setFile] = useState(null);

  const fetchProfile = async () => {
    if (!token) {
      setError("You are not authenticated or token expired!");
      setProfile(null);
      return;
    }

    try {
      const profileRes = await axios.get(SERVER_URL + "/api/v1/profile", {
        headers: {
          Authorization: `Bearer ${token}` // Добавляем токен в заголовок
        },
        withCredentials: true
      });
      setProfile(profileRes.data); // Сохраняем данные профиля
    } catch (error) {
      setError(`Error fetching profile: ${error.response?.data?.message || error.message}`);
    }
  };

  useEffect(() => {
    fetchProfile(); // Загружаем данные профиля при монтировании компонента
  }, []);

  const handleLogout = () => {
    setToken("");
    localStorage.removeItem("token");
    setProfile(null);
    setError("");
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]); // Сохраняем выбранный файл в состояние
  };

  const handleUpload = async () => {
    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const reader = new FileReader();
    reader.onload = async () => {
      const base64File = reader.result.split(",")[1]; // Получаем содержимое файла в Base64

      const payload = {
        fileName: file.name,
        fileContent: base64File
      };

      try {
        const res = await axios.post(SERVER_URL + "/api/v1/upload", payload, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}` // Добавляем токен в заголовок
          }
        });
        alert("File uploaded successfully: " + res.data);
      } catch (error) {
        setError(`Error uploading file: ${error.response?.data?.message || error.message}`);
      }
    };
    reader.readAsDataURL(file); // Читаем файл как Base64
  };

  return (
    <div>
      <h1>Profile</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {profile ? (
        <div>
          <p><strong>First Name:</strong> {profile.firstName}</p>
          <p><strong>Last Name:</strong> {profile.lastName}</p>
          <p><strong>Activated:</strong> {profile.activated ? "Yes" : "No"}</p>
          <button onClick={handleLogout} style={{ marginTop: "20px" }}>
            Logout
          </button>
          <div style={{ marginTop: "20px" }}>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload} style={{ marginLeft: "10px" }}>
              Upload File
            </button>
          </div>
        </div>
      ) : (
        !error && <p>Loading profile...</p>
      )}
    </div>
  );
};

export default Profile;
