import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { AuthProvider, useAuth } from "./api/AuthContext";

const SERVER_URL = "http://localhost:8081";

const AuthenticationComponent = () => {

  const { setToken } = useAuth();
  const navigate = useNavigate();

  const [loginData, setLoginData] = useState({
    login: "",
    password: ""
  });

  const [response, setResponse] = useState("");
  const [profile, setProfile] = useState(null); // Для хранения данных профиля
  //const [token, setToken] = useState(""); // Для хранения токена

  const handleLoginChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value
    });
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(SERVER_URL + "/api/v1/auth/authenticate", loginData);
      const token = res.data.token; // Получаем токен из ответа
      //setToken(token);
      setResponse(`Authentication Successful, token: ${token}`);
      navigate("/profile");
    } catch (error) {
      setResponse(`Error: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleFetchProfile = async () => {
    try {
      const profileRes = await axios.get(SERVER_URL + "/api/v1/profile", {
        headers: {
          Authorization: `Bearer ${token}` // Добавляем токен в заголовок
        },
        withCredentials: true
      });
      setProfile(profileRes.data); // Сохраняем данные профиля
    } catch (error) {
      setResponse(`Error fetching profile: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <div>
      <h1>Authentication</h1>

      <form onSubmit={handleLoginSubmit}>
        <input
          type="text"
          name="login"
          placeholder="Login"
          value={loginData.login}
          onChange={handleLoginChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={loginData.password}
          onChange={handleLoginChange}
          required
        />
        <button type="submit">Login</button>
      </form>

      <h3>Response</h3>
      <p>{response}</p>

      {token && (
        <button onClick={handleFetchProfile}>Profile</button>
      )}

      {profile && (
        <div>
          <h3>Profile</h3>
          <pre>{JSON.stringify(profile, null, 2)}</pre> {/* Отображаем данные профиля */}
        </div>
      )}
    </div>
  );
};

export default AuthenticationComponent;
